import React, { useState } from 'react';

function SignUp() {
  const [email, setEmail] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [emailError, setEmailError] = useState('');

  // 이메일 중복 확인 함수
  const checkEmailDuplication = async (email) => {
    const response = await fetch(`http://localhost:8080/api/signup`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ email }),
    });

    const result = await response.text();
    if (result === "이메일이 중복되었습니다.") {
      setEmailError("이메일이 중복되었습니다.");
    } else {
      setEmailError('');
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
  
    if (emailError) {
      alert(emailError); // 중복된 이메일일 경우 처리
      return;
    }
  
    // DTO에 맞는 객체로 데이터를 구성
    const userDTO = {
      email,
      username,
      password,
    };
  
    console.log('회원가입:', userDTO);
  
    // 서버로 회원가입 요청 보내는 로직
    fetch('http://localhost:8080/api/signup', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(userDTO),
    })
      .then((response) => {
        if (!response.ok) {
          // 응답이 성공적이지 않으면 오류를 발생시킴
          throw new Error('서버 오류');
        }
        return response.text(); // 텍스트 형식으로 응답을 받음
      })
      .then((data) => {
        if (data.success) {
          console.log('회원가입 성공:', data.message);
          alert(data.message);
        } else {
          console.log('회원가입 실패:', data.message);
          alert(data.message);
        }
      })
      .catch((error) => {
        console.error('회원가입 실패:', error);
        alert('회원가입 실패');
      });
  };

  return (
    <div className="signup-container">
      <h2>회원가입</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            onBlur={() => checkEmailDuplication(email)} // 이메일 입력 후 중복 체크
            required
          />
          {emailError && <p style={{ color: 'red' }}>{emailError}</p>}
        </div>
        <div>
          <label>Username:</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">회원가입</button>
      </form>
    </div>
  );
}

export default SignUp;
