import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function ProductInput() {
    const [productnum, setProductnum] = useState('');
    const [arrivaldate, setArrivaldate] = useState('');
    const [price, setPrice] = useState('');
    const [category, setCategory] = useState('');
    const [imageurl, setImageurl] = useState(null);
    const [error, setError] = useState('');
    const navigate = useNavigate();

    // 폼 제출
    const handleSubmit = async (e) => {
        e.preventDefault();
        console.log('폼 제출 시 실행됨');

        if (!productnum || !arrivaldate || !price || !category || !imageurl) {
            setError('입력 값을 비우지 마세요.');
            return;
        }

        const formData = new FormData();
        formData.append('productnum', productnum);
        formData.append('arrivaldate', arrivaldate);
        formData.append('price', price);
        formData.append('category', category);
        formData.append('imageurl', imageurl);

        formData.forEach((value, key) => {
            console.log(`${key}:`, value);
        });

        try {
            console.log('FormData:', formData);
            const response = await fetch('http://localhost:8080/api/productinput', {
                method: 'POST',
                body: formData,
            });

            if (!response.ok) {
                throw new Error('상품 추가 실패');
            }

            const data = await response.json();
            console.log('상품 추가 완료', data);
            navigate('/productinput');
        } catch (error) {
            console.error('Error:', error);
            setError('상품 추가 중 오류 발생');
        }
    };

    // 이미지 파일 선택 시 상태 업데이트
    const handleImageChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            setImageurl(file);
            console.log('선택된 파일:', file);
        }
    };

    return (
        <div>
            <h2>상품 등록</h2>
            <form onSubmit={handleSubmit}>
                <input 
                    type="text" 
                    value={category} 
                    onChange={(e) => setCategory(e.target.value)} 
                    placeholder="카테고리" 
                />
                <input 
                    type="text" 
                    value={productnum} 
                    onChange={(e) => setProductnum(e.target.value)} 
                    placeholder="제품 번호" 
                />
                <input 
                    type="date" 
                    value={arrivaldate} 
                    onChange={(e) => setArrivaldate(e.target.value)} 
                    placeholder="입고일" 
                />
                <input 
                    type="number" 
                    value={price} 
                    onChange={(e) => setPrice(e.target.value)} 
                    placeholder="가격" 
                />
                <input 
                    type="file" 
                    onChange={handleImageChange} 
                />
                {error && <p style={{ color: 'red' }}>{error}</p>}
                <button type="submit">등록하기</button>
            </form>
        </div>
    );
}

export default ProductInput;
