import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './ProductInput.css'; // 스타일 파일 추가

function ProductInput() {
    const [productnum, setProductnum] = useState('');
    const [arrivaldate, setArrivaldate] = useState('');
    const [price, setPrice] = useState('');
    const [category, setCategory] = useState('');
    const [imageurl, setImageurl] = useState(null);
    const [error, setError] = useState('');
    const [showCategories, setShowCategories] = useState(false); // 카테고리 드롭다운 표시 여부
    const navigate = useNavigate();

    // 카테고리 목록
    const categories = ["전자", "가전", "음식", "건강"];

    // 폼 제출
    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!productnum || !arrivaldate || !price || !category || !imageurl) {
            setError('입력 값을 비우지 마세요.');
            return;
        }

        const formData = new FormData();
        formData.append('productnum', productnum);
        formData.append('arrivaldate', arrivaldate);
        formData.append('price', price);
        formData.append('category', category);
        formData.append('imageurl', imageurl);

        try {
            const response = await fetch('http://localhost:8080/api/productinput', {
                method: 'POST',
                body: formData,
            });

            if (!response.ok) {
                throw new Error('상품 추가 실패');
            }

            const data = await response.json();
            navigate('/productinput');
        } catch (error) {
            console.error('Error:', error);
            setError('상품 추가 중 오류 발생');
        }
    };

    // 이미지 파일 선택 시 상태 업데이트
    const handleImageChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            setImageurl(file);
        }
    };

    // 카테고리 선택 토글
    const toggleCategoryDropdown = () => {
        setShowCategories(!showCategories);
    };

    // 카테고리 선택
    const handleCategorySelect = (selectedCategory) => {
        setCategory(selectedCategory);
        setShowCategories(false); // 선택 후 드롭다운 닫기
    };

    return (
        <div className="product-input-container">
            <h2>상품 등록</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    value={productnum}
                    onChange={(e) => setProductnum(e.target.value)}
                    placeholder="제품 번호"
                />
                <input
                    type="date"
                    value={arrivaldate}
                    onChange={(e) => setArrivaldate(e.target.value)}
                    placeholder="입고일"
                />
                <input
                    type="number"
                    value={price}
                    onChange={(e) => setPrice(e.target.value)}
                    placeholder="가격"
                />

                {/* 카테고리 선택 버튼 */}
                <div className="category-selector">
                    <button type="button" onClick={toggleCategoryDropdown}>
                        {category ? `선택된 카테고리: ${category}` : '카테고리 선택'}
                    </button>

                    {/* 카테고리 목록 표시 */}
                    {showCategories && (
                        <div className="category-dropdown">
                            {categories.map((cat) => (
                                <div
                                    key={cat}
                                    onClick={() => handleCategorySelect(cat)}
                                    className={category === cat ? 'selected-category' : ''}
                                >
                                    {cat}
                                </div>
                            ))}
                        </div>
                    )}
                </div>

                <input type="file" onChange={handleImageChange} />
                {error && <p className="error-message">{error}</p>}
                <button type="submit">등록하기</button>
            </form>
        </div>
    );
}

export default ProductInput;
