import React, { useState, useEffect } from 'react';
import '../cssfile/UserList.css';  // UserList.css 파일을 임포트

function UserList() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    // API 호출하여 회원 목록 가져오기
    fetch('http://localhost:8080/api/users')
      .then((response) => response.json())
      .then((data) => setUsers(data))
      .catch((error) => console.error('Error fetching users:', error));
  }, []);

  const formatDate = (dateString) => {
    if (!dateString) {
      return 'Invalid Date';
    }
  
    // '2024-11-17 10:30:09'을 '2024-11-17T10:30:09'로 변환
    const formattedDate = dateString.replace(' ', 'T');
  
    // Date 객체로 변환
    const date = new Date(formattedDate);
  
    return date.toISOString().split('T')[0]; // "yyyy-MM-dd" 형식으로 변환
  };

  return (
    <div className="users-container">
      <h1>회원 목록</h1>
      <table>
        <thead>
          <tr>
            <th>아이디</th>
            <th>이메일</th>
            <th>사용자 이름</th>
            <th>권한</th>
            <th>계정 생성일</th>
            <th>계정 변경일</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td>{user.id}</td> {/* ID */}
              <td>{user.email}</td> {/* 이메일 */}
              <td>{user.username}</td> {/* 사용자 이름 */}
              <td>{user.role}</td> {/* 권한 */}
              <td>{formatDate(user.createdAt)}</td> {/* 계정 생성일 */}
              <td>{formatDate(user.updatedAt)}</td> {/* 계정 변경일 */}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default UserList;
