import React, { useState, useEffect } from 'react';
import '../cssfile/Shop.css';

function Shop() {
  const [products, setProducts] = useState([]); // 제품 목록 상태
  const [selectedCategory, setSelectedCategory] = useState(''); // 선택된 카테고리
  const [isLoading, setIsLoading] = useState(false); // 로딩 상태

  // 전체 제품 데이터를 가져오는 함수
  const fetchAllProducts = async () => {
    setIsLoading(true);
    try {
      const response = await fetch('http://localhost:8080/api/products/all'); // 모든 제품 API 호출
      const data = await response.json();
      setProducts(data);
      setSelectedCategory(''); // 카테고리 선택 해제
    } catch (error) {
      console.error('전체 제품 데이터 로딩 실패:', error);
    } finally {
      setIsLoading(false);
    }
  };

  // 특정 카테고리의 제품 데이터를 가져오는 함수
  const fetchProductsByCategory = async (category) => {
    if (!category) return;

    setIsLoading(true);
    try {
      const response = await fetch(`http://localhost:8080/api/products?category=${category}`);
      const data = await response.json();
      setProducts(data);
    } catch (error) {
      console.error('카테고리별 제품 데이터 로딩 실패:', error);
    } finally {
      setIsLoading(false);
    }
  };

  // 초기 로딩 시 전체 제품 목록 가져오기
  useEffect(() => {
    fetchAllProducts();
  }, []);

  // 카테고리 선택 시 해당 카테고리의 제품 목록 가져오기
  useEffect(() => {
    if (selectedCategory) {
      fetchProductsByCategory(selectedCategory);
    }
  }, [selectedCategory]);

  return (
    <div className="shop-content">
      <div className="category-bar">
        <h2>카테고리아아아아아</h2>
        <div className="category-links">
          <span onClick={() => setSelectedCategory('전자')}>전자</span>
          <span onClick={() => setSelectedCategory('가전')}>가전</span>
          <span onClick={() => setSelectedCategory('음식')}>음식</span>
          <span onClick={() => setSelectedCategory('건강')}>건강</span>
        </div>
      </div>

      {/* 제품 목록 */}
      <div className="shop-space">
        {isLoading ? (
          <p>로딩 중. . .</p>
        ) : (
          products.map((product) => (
            <div className="space-item" key={product.productnum}>
              <img
                src={`${product.imageUrl}`}
                alt={product.productnum}
                className="product-image"
              />
              <h3>{product.price}</h3>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default Shop;
