import React, {useState, useEffect} from 'react';
import '../cssfile/Shop.css';

function Shop() {
  const [products, setProducts] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [isLoading, setIsLoading] = useState(false);//비동기 작업(카테고리 선택 후 제품 불러오는)의 처리 유무를 나타냄. false = 선택X 혹은 미처리. true = 처리 중.....

  //카테고리 변경 시 즉시 상태 변경 useState 변수 사용

  const fetchProductsByCategory = async (category) => {
    if (!category) return;//카테고리 선택 안했을 시 함수 작동 X (이벤트 동작 디폴트로 잠궈놓는 느낌)

    setIsLoading(true);//카테고리 선택 시 로딩 값 트루로 변경
    try {
      const response = await fetch(`http://localhost:8080/api/products?category=${category}`);
      const data = await response.json();//상품 데이터 저장할 data
      setProducts(data);//setProducts 변수에 상품 데이터 저장
    } catch(error){
      console.error('제품 데이터 로딩 실패:', error);
    }finally{//성공했든 안했든 끝났으면?
      setIsLoading(false);//미처리 상태로 돌아가라
    }
  };

  //카테고리 선택 후 해당 카테고리에 대한 요청 보내는 함수(fetchProductsByCategory) 작동.
  //selectedCategory 값이 변경되면, 그 값의 변화에 반응하여 useEffect가 자동으로 실행 
  useEffect(() => {
    if(selectedCategory){//빈 값이 아닌 이상 카테고리 선택 시 true 됨.
      fetchProductsByCategory(selectedCategory);//해당 카테고리 매개변수를 가지고 카테고리에 맞는 상품 가져오는 fetchProductByCategory 함수 실행
    }
  },[selectedCategory]);//상태 변화 조건으로 카테고리 값이 변경될 때 마다를 줌. 즉, 카테고리 값 변경될 때 마다 해당 작업 실행.

  useEffect(() =>{
    console.log('상품 데이터:', products);
  },[products]);

  return (
    <div className="shop-content">
      {/* 카테고리 영역 */}
      <div className="category-bar">
        <h2>카테고리</h2>
        <div className="category-links">
          <span onClick={() => setSelectedCategory('전자')}>전자</span>
          <span onClick={() => setSelectedCategory('가전')}>가전</span>
          <span onClick={() => setSelectedCategory('음식')}>음식</span>
          <span onClick={() => setSelectedCategory('건강')}>건강</span>
        </div>
      </div>

      {/* 제품 목록 */}
      <div className="shop-space">
        {isLoading ? (
          <p>로딩 중. . .</p>
        ): (
          products.map((product) => (
            <div className="space-item" key={product.productnum}>
              <img
                src={`http://localhost:3000${product.imageurl}`}
                alt={product.productnum}
                className="product-image"
                />
                <h3>{product.price}</h3>
              </div>
          ))
        )}
      </div>
    </div>
  );
}
  

export default Shop;
