import { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [message, setMessage] = useState(''); // 메시지 상태 관리
  const [users, setUsers] = useState([]); // 사용자 목록 상태 관리

  // 컴포넌트가 마운트될 때 서버로 요청 보내기
  useEffect(() => {
    // 메시지 요청
    fetch('http://localhost:8080/api/message')
      .then((response) => response.text())
      .then((data) => {
        setMessage(data); // 응답 메시지를 상태에 저장
      })
      .catch((error) => {
        console.error('Error:', error); // 오류 처리
      });

    // 사용자 목록 요청
    fetch('http://localhost:8080/api/users')
      .then((response) => response.json()) // JSON 응답 처리
      .then((data) => {
        setUsers(data); // 사용자 목록을 상태에 저장
      })
      .catch((error) => {
        console.error('Error:', error); // 오류 처리
      });
  }, []); // 빈 배열을 넣으면 컴포넌트가 처음 마운트될 때 한 번만 실행

  return (
    <div className="App">
      <header className="App-header">
        <h1>{message}</h1> {/* 서버에서 받은 메시지를 출력 */}

        <h2>User List</h2>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Email</th>
              <th>Username</th>
              <th>Role</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td>{user.email}</td>
                <td>{user.username}</td>
                <td>{user.role}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </header>
    </div>
  );
}

export default App;
