import React, { useState } from 'react'; // 상태 변화 변수
import { useNavigate } from 'react-router-dom'; // 홈 링크 사용하기 위해 네비게이터 선언(라우팅 url 사용할 수 있음)

function ProoductInput() {
    const [productnum, setProductnum] = useState('');
    const [arrivaldate, setArrivaldate] = useState('');
    const [price, setPrice] = useState('');
    const [category, setCategory] = useState('');
    const [imageurl, setImageurl] = useState(null); // 초기값을 null로 설정
    const [error, setError] = useState('');
    const navigate = useNavigate();

    // 폼 제출
    const handleSubmit = async (e) => {
        e.preventDefault();
        console.log('폼 제출 시 실행됨');  // 추가된 로그

        // 폼 검증 (다 입력해야함)
        if (!productnum || !arrivaldate || !price || !category || !imageurl) { // 하나라도 빠졌으면?
            setError('입력 값을 비우지 마세요.');
            return;
        }

        // FormData에 이미지 파일을 올바르게 추가
        const formData = new FormData();
        formData.append('productnum', productnum);
        formData.append('arrivaldate', arrivaldate);
        formData.append('price', price);
        formData.append('category', category);
        formData.append('imageurl', imageurl); // 이미지 파일

        formData.forEach((value, key) => {
            console.log(`${key}:`, value);  // 추가된 데이터 확인
        });

        try {
            console.log('FormData:', formData); // 폼 데이터 확인
            const response = await fetch('http://localhost:8080/api/productinput', {
                method: 'POST',
                body: formData,
            });

            if (!response.ok) {
                throw new Error('상품 추가 실패');
            }

            const data = await response.json(); // 서버에서 이미지 경로 반환. 저장 완료
            console.log('상품 추가 완료', data); // 확인을 위해 응답받은 상품 정보 출력
            navigate('/productinput'); // 돌아가
        } catch (error) {
            console.error('Error :', error);
            setError('상품 추가중 오류 발생');
        }
    };

    // 이미지 파일 선택 시 상태 업데이트
    const handleImageChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            setImageurl(file); // 선택된 파일을 state에 저장
            console.log('선택된 파일:', file); // 선택된 파일을 로그로 확인
        }
    };

    return (
        <div>
            <h2>상품 추가</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>제품 번호:</label>
                    <input
                        type="text"
                        value={productnum}
                        onChange={(e) => setProductnum(e.target.value)}
                    />
                </div>
                <div>
                    <label>입고일:</label>
                    <input
                        type="date"
                        value={arrivaldate}
                        onChange={(e) => setArrivaldate(e.target.value)}
                    />
                </div>
                <div>
                    <label>가격:</label>
                    <input
                        type="number"
                        value={price}
                        onChange={(e) => setPrice(e.target.value)}
                    />
                </div>
                <div>
                    <label>카테고리:</label>
                    <input
                        type="text"
                        value={category}
                        onChange={(e) => setCategory(e.target.value)}
                    />
                </div>
                <div>
                    <label>상품 이미지:</label>
                    <input
                        type="file"
                        onChange={handleImageChange} // 이미지 파일 선택 시 상태 업데이트
                    />
                </div>
                {error && <p style={{ color: 'red' }}>{error}</p>}
                <button type="submit">상품 추가</button>
            </form>
        </div>
    );
}

export default ProoductInput;
