import React from 'react';
import './App.css';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';  // 필요한 컴포넌트 import
import Header from './components/Header';
import Shop from './components/Shop';
import Footer from './components/Footer';  // Footer 컴포넌트 추가
import ProductDetail from './components/ProductDetail';

function App() {
  return (
    <div className="App">
      <Router>
        {/* 헤더 컴포넌트 */}
        <Header />

        <Routes>
          <Route path="/" element={<Shop />} />
          <Route path="/product/:productId" element={<ProductDetail />} /> {/* 상품 상세 페이지 라우트 추가 */}
          {/* 다른 라우트들 */}
        </Routes>

        {/* 푸터 컴포넌트 */}
        <Footer />
      </Router>
    </div>
  );
}

export default App;
