import React, { useState } from 'react';

function SignUp() {
  const [email, setEmail] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [role] = useState('USER'); // 기본값: 'USER'
  
  // 현재 날짜를 YYYY-MM-DD 형식으로 설정
  const [createAt] = useState(new Date().toISOString());
  const [updateAt] = useState(new Date().toISOString());

  const handleSubmit = (e) => {
    e.preventDefault();

    // DTO에 맞는 객체로 데이터를 구성
    const userDTO = {
      email,
      username,
      password,
      role,        // 기본값 'USER'
      createAt,    // 자동으로 생성되는 값
      updateAt,    // 자동으로 생성되는 값
    };

    console.log('회원가입:', userDTO);

    // 서버로 회원가입 요청 보내는 로직
    fetch('/api/signup', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json', // 요청 본문 형식
      },
      body: JSON.stringify(userDTO), // 객체를 JSON 문자열로 변환하여 전송
    })
      .then((response) => response.json()) // 응답을 JSON 형식으로 파싱
      .then((data) => {
        console.log('회원가입 성공:', data);
        alert('회원가입 성공');
      })
      .catch((error) => {
        console.error('회원가입 실패:', error);
        alert('회원가입 실패');
      });
  };

  return (
    <div className="signup-container">
      <h2>회원가입</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Username:</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">회원가입</button>
      </form>
    </div>
  );
}

export default SignUp;
